def add(a, b):
    """Function to sum two numbers."""
    print("Adding two numbers.")
    return a + b

def subtract(a, b):
    """Function to subtract two numbers."""
    print("Subtracting two numbers.")
    return a - b

def multiply(a, b):
    """Function to multiply two numbers."""
    print("Multiplying two numbers.")
    return a * b

def divide(a, b):
    """Function to divide two numbers."""
    print("Dividing two numbers.")
    return a / b

def remainder(a, b):
    """Function to get the remainder."""
    print("Calculating remainder of two numbers.")
    return a % b  

def exponent(a, b):
    """Function to calculate a to the power of b."""
    print("Calculating exponentiation of two numbers.")
    return a ** b

